import React from 'react';
import { Table, Button, Alert,Col,Row } from 'react-bootstrap';

class App_Skill extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      error: null,
      app_skills: [],
      response: {}
    }
  }

  componentDidMount() {
    const apiUrl = 'https://localhost:44383/api/Skill/GetSkills';

    fetch(apiUrl)
      .then(res => res.json())
      .then(
        (result) => {
          this.setState({
            app_skills: result
          });
        },
        (error) => {
          this.setState({ error });
        }
      )
  }

deleteSkill = (skill_id) => {
    const { app_skills } = this.state;
    const apiUrl = 'https://localhost:44383/api/Skill/Delete?idSkill=' + skill_id ;
    const options = {
      method: 'DELETE',
    }

    fetch(apiUrl, options);
    const newapp_skill = app_skills
    .filter((app_skills) => app_skills.skillId !== skill_id)
    .map((filterapp_skill) => {
        return filterapp_skill;
    });

    this.setState({
        app_skills: newapp_skill
    });
  }


  Create_Skill = (event) => {
    event.preventDefault();
    alert("Berhasil menambahkan " + this.state.skillName);

    const { app_skills } = this.state;
    fetch('https://localhost:44383/api/Skill/Create', {
    method: 'POST',
    headers: {
        'Accept': 'application/json',
        'Content-Type': 'application/json',
    },
    body: JSON.stringify({
        SkillName: this.state.skillName

    })
    })

    fetch(apiUrl, options);
  }
  
  Change_Skill = (event) => {
    this.setState({skillName: event.target.value});
  }

  render() {
    const { error, app_skills} = this.state;

    if(error) {
      return (
        <div>Error: {error.message}</div>
      )
    } else {
      return(
        <div>
          <h2>Skill</h2>
          {this.state.response.message && <Alert variant="info">{this.state.response.message}</Alert>}
          <Row>
            <Col md={3}>
                <Table bordered hover>
                    <thead>
                        <tr>
                            <th>Skill ID</th>
                            <th>Skill Name</th>
                            <th>Action</th>
                            
                        </tr>
                    </thead>
                    <tbody>
                    {app_skills.map(data => (
                        <tr key={data.skillId}>
                            <td>{data.skillId}</td>
                            <td>{data.skillName}</td>
                            <td>
                                {/* <Button variant="info" onClick={() => this.props.editProduct(product.id)}>Edit</Button> */}
                                &nbsp;<Button variant="danger" onClick={() => this.deleteSkill(data.skillId)}>Delete</Button>
                            </td>
                        </tr>
                    ))}
                </tbody>
                </Table>
                <form onSubmit={this.Create_Skill}>
                <h1>Hello {this.state.username}</h1>
                    <p>Enter your name, and submit:</p>
                    <input
                        type='text'
                        onChange={this.Change_Skill}
                    />
                    <input type='submit'/>
                </form>
            </Col>
          </Row>
        </div>
      )
    }
  }
}

export default App_Skill;
