import React from 'react';
import { Table, Button, Alert,Col,Row } from 'react-bootstrap';

class App_Skill extends React.Component {
  constructor(props) {
    super(props);
    this.removeItem = this.removeItem.bind(this)
    this.state = {
      error: null,
      app_skills: [],
      response: {}
    }
  }

  componentDidMount() {
    const apiUrl = 'https://localhost:44383/api/Skill/GetSkills';

    fetch(apiUrl)
      .then(res => res.json())
      .then(
        (result) => {
          this.setState({
            app_skills: result
          });
        },
        (error) => {
          this.setState({ error });
        }
      )
  }

deleteSkill = (skill_id) => {
    const { app_skills } = this.state;
    const apiUrl = 'https://localhost:44383/api/Skill/Delete?idSkill=' + skill_id ;
    const options = {
      method: 'DELETE',
    }

    fetch(apiUrl, options);
    const newSkill = this.app_skills
    .filter((makanan) => makanan.id !== id)
    .map((filterMakanan) => {
        return filterMakanan;
    });

    this.setState({
        makanan: newSkill
    });
  }

  render() {
    const { error, app_skills} = this.state;

    if(error) {
      return (
        <div>Error: {error.message}</div>
      )
    } else {
      return(
        <div>
          <h2>Skill</h2>
          {this.state.response.message && <Alert variant="info">{this.state.response.message}</Alert>}
          <Row>
            <Col md={3}>
                <Table bordered hover>
                    <thead>
                        <tr>
                            <th>Skill ID</th>
                            <th>Skill Name</th>
                            <th>Action</th>
                            
                        </tr>
                    </thead>
                    <tbody>
                    {app_skills.map(data => (
                        <tr key={data.skillId}>
                            <td>{data.skillId}</td>
                            <td>{data.skillName}</td>
                            <td>
                                {/* <Button variant="info" onClick={() => this.props.editProduct(product.id)}>Edit</Button> */}
                                &nbsp;<Button variant="danger" onClick={() => this.deleteSkill(index)}>Delete</Button>
                            </td>
                        </tr>
                    ))}
                </tbody>
                </Table>
            </Col>
          </Row>
        </div>
      )
    }
  }
}

export default App_Skill;
