import React from 'react';
import { Table, Button, Alert } from 'react-bootstrap';

class App_Skill extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      error: null,
      app_skills: [],
      response: {}
    }
  }

  componentDidMount() {
    const apiUrl = 'https://localhost:5001/api/Skill/GetSkills';

    fetch(apiUrl)
      .then(res => res.json())
      .then(
        (result) => {
          this.setState({
            app_skills: result
          });
        },
        (error) => {
          this.setState({ error });
        }
      )
  }

