//             response: result,
//             app_skills: app_skills.filter(product => product.id !== productId)
//           });
//         },
//         (error) => {
//           this.setState({ error });
//         }
//       )
//   }

  render() {
    const { error, app_skills} = this.state;

    if(error) {
      return (
        <div>Error: {error.message}</div>
      )
    } else {
      return(
        <div>
          <h2>Product List</h2>
          {this.state.response.message && <Alert variant="info">{this.state.response.message}</Alert>}
          <Table>
            <thead>
              <tr>
                <th>#ID</th>
                <th>Product Name</th>
                <th>SKU</th>

              </tr>
            </thead>
            <tbody>
              {app_skills.map(data => (
                <tr key={data.id}>
                  <td>{data.id}</td>
                  <td>{data.data_name}</td>
                  <td>
                    {/* <Button variant="info" onClick={() => this.props.editProduct(product.id)}>Edit</Button>
                    &nbsp;<Button variant="danger" onClick={() => this.deleteProduct(product.id)}>Delete</Button> */}
                  </td>
                </tr>
              ))}
            </tbody>
          </Table>
        </div>
      )
    }
  }
}

export default App_Skill;
